import React from 'react';

import PropTypes from 'prop-types';

import MyPagination from '../Pagination';
import MovieCard from '../MovieCard';
import ApiService from '../ApiService';

import ErrorIndicator from '../ErrorIndicator';

export default class Content extends React.Component { // ({ title }) {

  constructor() {
    super()
    this.state = {
      moviesList: null,
      error: false,
    }
    this.apiService = new ApiService();
  }

  componentDidMount() {
    const { title } = this.props;
    if (title !== null) {
      this.apiService
        .getAllMovies(title)
        .then((moviesList) => {
          if (moviesList.length !== 0) {
            this.setState(() => ({
              moviesList,
              error: false
            }));
          } else {
            this.setState(() => ({ error: 'emptyData' }));
          }
        })
        .catch((err) => {
          this.onError(err);
        });
    }
  }

  onError = (err) => {
    this.setState({
      error: err.name,
    });
  };

  renderItems = () => {
    const { moviesList } = this.state;
    return (
      <div className="movies-list">
        {moviesList.map((movie) => (
          <MovieCard key={movie.id} data={movie} />
        ))}
      </div>
    );
  }

  render() {
    const { title } = this.props;
    const { error } = this.state;

    const errorMessage = error ? <ErrorIndicator error={error} /> : null;
    const content = !error ? this.renderItems() : null;
    const spinner = !error ? <Spin size="large" /> : null;

    if (title !== null) {
      console.log('render 1')
      return (
        <>
          { content }
          { errorMessage }
          { spinner }
          <MyPagination />
        </>
      );
    }
    console.log('render 2')
    return null;
  }
}

Content.defaultProps = {
  title: '',
};

Content.propTypes = {
  title: PropTypes.string,
};
