import React from "react";

import PropTypes from 'prop-types';

import ApiService from "../ApiService";


export default class MovieGenres extends React.Component {
  apiService = new ApiService();

  constructor() {
    super();
    this.state = {
      tagsList: null
    }
  }

  componentDidMount() {
    const { genreIds } = this.props;
    this.getItems(genreIds);
  }

  getItems = (tags) => {
    this.apiService.getMovieGenres(tags).then(res => {
        this.setState({
          tagsList: res,
        })
        console.log(res)
        }
      )
  }

  renderItems = () => {
    const { tagsList } = this.state;
    if (tagsList.length > 0) {
      console.log('renderin: ', tagsList)
      return (
          <div className="tag">
             {tagsList.map((tag) => 
            (
              <span key = {tag}>{tag}</span>
            )
            )}
          </div>
      )
    }
      return null;
  };

  render() {
    return (
      <div className="movie-tags">
        {this.renderItems}
      </div>
    )
  }
}

MovieGenres.defaultProps = {
  genreIds: [],
}
MovieGenres.propTypes = {
  genreIds: PropTypes.arrayOf(PropTypes.number)
}
