import React from "react";

import PropTypes from 'prop-types';

import ApiService from "../ApiService";


export default class MovieGenres extends React.Component {
  apiService = new ApiService();

  constructor() {
    super();
    this.state = {
      tagsList: null
    }
  }

  componentDidMount() {
    const { genreIds } = this.props;
    this.getItems(genreIds);
  }

  // отловить ошибку на картинку

  getItems = (tags) => {
    this.apiService.getMovieGenres(tags)
      .then(res => {
        this.setState({
          tagsList: res,
        })
        }
      )
      .catch(err => {
        this.onError(err)
      })
  }

  onError = () => {
    this.setState({
      error: 'emptyData',
    });
  };

  renderItems = () => {
    const { tagsList } = this.state;
    if (tagsList) {

      return (
        tagsList.map((tag) => 
        (
          <div key = {tag} className="tag">
            <span >{tag}</span>
          </div>
        ))
      )
    }
      return null;
  };

  render() {

    return (
      <div className="movie-tags">
        {this.renderItems()}
      </div>
    )
  }
}

MovieGenres.defaultProps = {
  genreIds: [],
}
MovieGenres.propTypes = {
  genreIds: PropTypes.arrayOf(PropTypes.number)
}
