import React from 'react';
import './movie-card.css';

import { format } from 'date-fns';

import { Rate } from 'antd';

import ApiService from '../ApiService';

import noimg from './noimg.png';

function MovieCard(params) {

  const apiService = new ApiService();

  function limitStr(str) {
    const newStr = str.split(' ').slice(0, 20).join(' ');
    return newStr.concat('...');
  }

  function getImg(url) {
    if (!url) {
      return noimg;
    }
    // return `https://image.tmdb.org/t/p/original${url}`;
    return noimg;
  }

  function formatDate(date) {
    if (!date) {
      return date;
    }
    return format(new Date(date), 'MMMM dd, yyyy');
  }

  const { data } = params;

  function addRatedMovie() {
    const myRaiting = 7;
    data.myRaiting = myRaiting;
    apiService.addRatedMovie(data)
  }

  const { myRaiting = 0 } = data;

  function isDisabled() {
    if (myRaiting) {
      return true;
    }
    return false;
  }


  return (
    <div className="movie-card">
      <img src={getImg(data.poster_path)} alt="img" />
      <div className="movie-params">
        <h3 className="movie-title">{data.title}</h3>
        <p className="movie-date">{formatDate(data.release_date)}</p>
        <div className="movie-tags">
          <div className="tag">
            <span>Action</span>
          </div>
          <div className="tag">
            <span>Drama</span>
          </div>
        </div>
        <p className="movie-overview">{limitStr(data.overview)}</p>
        <Rate 
          allowHalf 
          value={myRaiting}
          disabled={isDisabled()} 
          count={10} 
          style={{ fontSize: 14 }}
          onChange={() => addRatedMovie()} />
      </div>
    </div>
  );
}

export default MovieCard;
