import React from 'react';

import { Spin } from 'antd';

import PropTypes from 'prop-types';

import MyPagination from '../Pagination';
import MovieCard from '../MovieCard';
import ApiService from '../ApiService';

import ErrorIndicator from '../ErrorIndicator';

export default class Content extends React.Component { // ({ title }) {

  constructor() {
    super()
    this.state = {
      moviesList: null,
      error: false,
    }
    this.apiService = new ApiService();
  }

  componentDidMount() {
    this.getMoviesList()
  }

  getMoviesList = async (title = "batman", page = 1) => {
    this.apiService
      .getAllMovies(title, page)
      .then((moviesList) => {
        if (moviesList.results.length !== 0) {
          this.setState(() => ({
            moviesList,
            error: false
          }));
        } else {
          this.setState(() => ({ error: 'emptyData' }));
        }
      })
      .catch((err) => {
        this.onError(err);
      });
  }

  onError = (err) => {
    this.setState({
      error: err.name,
    });
  };

  changePage = (page) => {
    const { title } = this.props;
    this.getMoviesList(title, page)
  }

  renderItems = () => {
    const { moviesList } = this.state;
    return (
      <div className="movies-list">
        {moviesList.results.map((movie) => (
          <MovieCard key={movie.id} data={movie} />
        ))}
      </div>
    );
  }

  render() {
    const { title } = this.props;
    const { error, moviesList } = this.state;

    const errorMessage = error ? <ErrorIndicator error={error} /> : null;
    const content = !error && title !== null ? this.renderItems() : null;
    const spinner = !error && content === null ? <Spin size="large" /> : null;
    const pagination = !error && content !== null ? <MyPagination changePage = {(page) => this.changePage(page)}total = {moviesList.total_results}/> : null;

    if (title !== null) {
      return (
        <>
          { content }
          { errorMessage }
          { spinner }
          { pagination }
        </>
      );
    }
    return null;
  }
}

Content.defaultProps = {
  title: '',
};

Content.propTypes = {
  title: PropTypes.string,
};
