import React from 'react';
import { Input, Spin } from 'antd';
import debounce from 'lodash.debounce'

import TabsBar from '../TabsBar';
import Content from '../Content';
import ErrorIndicator from '../ErrorIndicator';

import './app.css';
// Сделать отображение на мобилке!!
export default class App extends React.Component {

  constructor() {
    super();
    this.state = {
      error: false,
      title: null,
      value: null,
      loading: false,
    };
    this.debouncedOnTitleEntered = debounce(title => this.onTitleEnetered(title), 1000);
  }


  onTitleEnetered = (title) => {
    this.setState({
      title,
      error: false,
      loading: false,
    })
  };

  handleChange = (e) => {
    const newValue = e.target.value;
    this.setState({
      value: newValue,
    });
    if(newValue.trim()) {
      this.setState({
        title: null,
        loading: true,
        error: false
      });
      this.debouncedOnTitleEntered(newValue.trim());
    }
  };

  render() {
    const { error, value, title, loading } = this.state;

    // eslint-disable-next-line react/jsx-filename-extension
    // const errorMessage = error ? <ErrorIndicator error={error} /> : null;
    const content = !loading && !error ? <Content title={title} /> : null;
    const spinner = loading && !error ? <Spin size="large" /> : null;

    return (
      <div className="movieapp-container">
        <TabsBar />
        <form id="search_panel" onSubmit={e => e.preventDefault()}>  {/* onSubmit={(e) => this.handleSubmit(e) */}
          <Input
            id="search_panel"
            placeholder="Type to search..."
            value={value}
            onChange={(e) => this.handleChange(e)}
          />
        </form>
        {/* {errorMessage} */}
        {content}
        {spinner}
      </div>
    );
  }
}
